// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IFurionSwapPair is IERC20 {

    function initialize(
        address _token0,
        address _token1
    ) external;

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function feeRate() external view returns (uint256);

    function deadline() external view returns (uint256);

    function getReserves()
        external
        view
        returns (uint112 _reserve0, uint112 _reserve1);

    function swap(
        uint256,
        uint256,
        address
    ) external;

    function burn(address) external returns (uint256, uint256);

    function mint(address) external returns (uint256);

    function sync() external;
}

// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

interface IFurionSwapV2Router {
    function factory() external view returns (address);
    function WETH() external view returns (address);

    function addLiquidity(
        address _tokenA,
        address _tokenB,
        uint _amountADesired,
        uint _amountBDesired,
        uint _amountAMin,
        uint _amountBMin,
        address _to,
        uint _deadline
    ) external returns (uint _amountA, uint _amountB, uint _liquidity);

    function addLiquidityETH(
        address _token,
        uint _amountTokenDesired,
        uint _amountTokenMin,
        uint _amountETHMin,
        address _to,
        uint _deadline
    ) external payable returns (uint _amountToken, uint _amountETH, uint _liquidity);

    function removeLiquidity(
        address _tokenA,
        address _tokenB,
        uint _liquidity,
        uint _amountAMin,
        uint _amountBMin,
        address _to,
        uint _deadline
    ) external returns (uint _amountA, uint _amountB);

    function removeLiquidityETH(
        address _token,
        uint _liquidity,
        uint _amountTokenMin,
        uint _amountETHMin,
        address _to,
        uint _deadline
    ) external returns (uint _amountToken, uint _amountETH);

    function swapExactTokensForTokens(
        uint _amountIn,
        uint _amountOutMin,
        address[] calldata _path,
        address _to,
        uint _deadline
    ) external returns (uint[] memory _amounts);

    function swapTokensForExactTokens(
        uint _amountOut,
        uint _amountInMax,
        address[] calldata _path,
        address _to,
        uint _deadline
    ) external returns (uint[] memory _amounts);

    function swapExactETHForTokens(
        uint _amountOutMin,
        address[] calldata _path,
        address _to,
        uint _deadline
    ) external payable returns (uint[] memory _amounts);

    function swapTokensForExactETH(
        uint _amountOut,
        uint _amountInMax,
        address[] calldata _path,
        address _to,
        uint _deadline
    ) external returns (uint[] memory _amounts);

    function swapExactTokensForETH(
        uint _amountIn,
        uint _amountOutMin,
        address[] calldata _path,
        address _to,
        uint _deadline
    ) external returns (uint[] memory amounts);

    function swapETHForExactTokens(
        uint _amountOut,
        address[] calldata _path,
        address _to,
        uint _deadline
    ) external payable returns (uint[] memory amounts);
}

