contracts/furion-farming/FarmingPoolUpgr.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {IFurionToken} from "../tokens/interfaces/IFurionToken.sol";
import {Math} from "../libraries/Math.sol";

/*
//===================================//
 ______ _   _______ _____ _____ _   _ 
 |  ___| | | | ___ \_   _|  _  | \ | |
 | |_  | | | | |_/ / | | | | | |  \| |
 |  _| | | | |    /  | | | | | | . ` |
 | |   | |_| | |\ \ _| |_\ \_/ / |\  |
 \_|    \___/\_| \_|\___/ \___/\_| \_/
//===================================//
* /

/**
 * @title  Farming Pool
 * @notice This contract is for LPToken mining on Furion
 * @dev    The pool id starts from 1 rather than 0
 *         The Furion reward is calculated by timestamp rather than block number
 *         This is one upgradeable version
 */

contract FarmingPoolUpgradeable is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable
{
    using SafeERC20 for IERC20;
    using SafeERC20 for IFurionToken;
    using Math for uint256;

    // ---------------------------------------------------------------------------------------- //
    // ************************************* Variables **************************************** //
    // ---------------------------------------------------------------------------------------- //

    string public constant name = "Furion LP Farming Pool";

    // The reward token is Furion
    IFurionToken public furion;

    // SCALE/Precision used for calculating rewards
    uint256 public constant SCALE = 1e12;

    // PoolId starts from 1
    uint256 public _nextPoolId;

    // Farming starts from a certain block timestamp
    uint256 public startTimestamp;

    struct PoolInfo {
        address lpToken; // LPToken address
        uint256 basicFurionPerSecond; // Basic Reward speed
        uint256 lastRewardTimestamp; // Last reward timestamp
        uint256 accFurionPerShare; // Accumulated Furion per share
    }
    PoolInfo[] public poolList;

    // lptoken address => poolId
    mapping(address => uint256) public poolMapping;

    // poolId => is farming or not
    mapping(uint256 => bool) public isFarming;

    struct UserInfo {
        uint256 rewardDebt; // Furion reward debt
        uint256 stakingBalance; // the amount of a user's staking in the pool
    }

    // poolId => userAddress => userInfo
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;

    // ---------------------------------------------------------------------------------------- //
    // *************************************** Events ***************************************** //
    // ---------------------------------------------------------------------------------------- //

    event StartTimestampChanged(uint256 startTimestamp);
    event Stake(address staker, uint256 poolId, uint256 amount);
    event Withdraw(address staker, uint256 poolId, uint256 amount);
    event Harvest(
        address staker,
        address rewardReceiver,
        uint256 poolId,
        uint256 pendingReward
    );

    event NewPoolAdded(address lpToken, uint256 basicFurionPerSecond);
    event FarmingPoolStarted(uint256 poolId, uint256 timestamp);
    event FarmingPoolStopped(uint256 poolId, uint256 timestamp);
    event FurionRewardChanged(uint256 poolId, uint256 basicFurionPerSecond);
    event PoolUpdated(uint256 poolId, uint256 accFurionPerShare);

    // ---------------------------------------------------------------------------------------- //
    // ************************************* Constructor ************************************** //
    // ---------------------------------------------------------------------------------------- //

    function initialize(address _furion) public initializer {
        require(_furion != address(0), "FARMING_POOL: ZERO_ADDRESS");

        __Ownable_init();
        __ReentrancyGuard_init_unchained();
        __Pausable_init_unchained();

        furion = IFurionToken(_furion);

        // Start from 1
        _nextPoolId = 1;

        poolList.push(
            PoolInfo({
                lpToken: address(0),
                basicFurionPerSecond: 0,
                lastRewardTimestamp: 0,
                accFurionPerShare: 0
            })
        );
    }

    // ---------------------------------------------------------------------------------------- //
    // ************************************** Modifiers *************************************** //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice The address can not be zero
     */
    modifier notZeroAddress(address _address) {
        require(_address != address(0), "FARMING_POOL: ZERO_ADDRESS");
        _;
    }

    /**
     * @notice The pool is still in farming
     */
    modifier stillFarming(uint256 _poolId) {
        require(isFarming[_poolId], "FARMING_POOL: POOL_NOT_FARMING");
        _;
    }

    // ---------------------------------------------------------------------------------------- //
    // *********************************** Main Functions ************************************* //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice Add a new lp into the pool
     * @dev Can only be called by the owner
     *      The reward speed can be 0 and set later by setFurionReward function
     * @param _lpToken LP token address
     * @param _basicFurionPerSecond Basic reward speed(per second) for this new pool
     * @param _withUpdate Whether update all pools' status
     */
    function add(
        address _lpToken,
        uint256 _basicFurionPerSecond,
        bool _withUpdate
    ) public notZeroAddress(_lpToken) onlyOwner whenNotPaused {
        // Check if already exists, if the poolId is 0, that means not in the pool
        require(!_alreadyInPool(_lpToken), "FARMING_POOL: ALREADY_IN_POOL");

        if (_withUpdate) {
            massUpdatePools();
        }

        uint256 lastRewardTimestamp = block.timestamp > startTimestamp
            ? block.timestamp
            : startTimestamp;

        // Push this new pool into the list
        poolList.push(
            PoolInfo({
                lpToken: _lpToken,
                basicFurionPerSecond: _basicFurionPerSecond,
                lastRewardTimestamp: lastRewardTimestamp,
                accFurionPerShare: 0
            })
        );

        // Store the poolId and set the farming status to true
        if (_basicFurionPerSecond > 0) isFarming[_nextPoolId] = true;

        poolMapping[_lpToken] = _nextPoolId++;

        emit NewPoolAdded(_lpToken, _basicFurionPerSecond);
    }

    /**
     * @notice Update the FurionPerSecond for a specific pool (set to 0 to stop farming)
     * @param _poolId Id of the farming pool
     * @param _basicFurionPerSecond New basic reward amount per second
     * @param _withUpdate Whether update all pools
     */
    function setFurionReward(
        uint256 _poolId,
        uint256 _basicFurionPerSecond,
        bool _withUpdate
    ) public onlyOwner whenNotPaused {
        // Ensure there already exists this pool
        require(
            poolList[_poolId].lastRewardTimestamp != 0,
            "FARMING_POOL: POOL_NOT_EXIST"
        );

        if (_withUpdate) massUpdatePools();
        else updatePool(_poolId);

        // Not farming now + reward > 0 => Restart
        if (isFarming[_poolId] == false && _basicFurionPerSecond > 0) {
            isFarming[_poolId] = true;
            emit FarmingPoolStarted(_poolId, block.timestamp);
        }

        if (_basicFurionPerSecond == 0) {
            isFarming[_poolId] = false;
            emit FarmingPoolStopped(_poolId, block.timestamp);
        } else {
            poolList[_poolId].basicFurionPerSecond = _basicFurionPerSecond;
            emit FurionRewardChanged(_poolId, _basicFurionPerSecond);
        }
    }

    /**
     * @notice Update the FurionPerSecond for a bundle of pools (used for daily updating farming rate)
     * @param _poolId Id collection of the farming pool
     * @param _basicFurionPerSecond New basic reward amount per second
     * @param _withUpdate Whether update all pools
     */
    function setFurionRewards(
        uint256[] calldata _poolId,
        uint256[] calldata _basicFurionPerSecond,
        bool _withUpdate
    ) public onlyOwner whenNotPaused {
        uint256 length = _poolId.length;
        require(length <= 9, "FARMING_POOL: MORE_THAN_NINE");

        for (uint256 i = 0; i < length; ) {
            setFurionReward(_poolId[i], _basicFurionPerSecond[i], _withUpdate);

            unchecked {
                ++i;
            }
        }
    }

    /**
     * @notice Stake LP token into the farming pool
     * @dev Can only stake to the pools that are still farming
     * @param _poolId Id of the farming pool
     * @param _amount Staking amount
     */
    function stake(uint256 _poolId, uint256 _amount)
        public
        nonReentrant
        whenNotPaused
        stillFarming(_poolId)
    {
        require(_amount > 0, "FARMING_POOL: STAKE_ZERO");

        PoolInfo storage pool = poolList[_poolId];
        UserInfo storage user = userInfo[_poolId][msg.sender];

        // Must update first
        updatePool(_poolId);

        // First distribute the reward if exists
        if (user.stakingBalance > 0) {
            uint256 pending = (user.stakingBalance * pool.accFurionPerShare) /
                SCALE -
                user.rewardDebt;

            // Real reward amount
            uint256 reward = _safeFurionTransfer(msg.sender, pending);
            emit Harvest(msg.sender, msg.sender, _poolId, reward);
        }

        // Actual deposit amount
        uint256 actualAmount = _safeLPTransfer(
            false,
            pool.lpToken,
            msg.sender,
            _amount
        );

        user.stakingBalance += actualAmount;

        user.rewardDebt =
            (user.stakingBalance * pool.accFurionPerShare) /
            SCALE;

        emit Stake(msg.sender, _poolId, actualAmount);
    }

    /**
     * @notice Withdraw lptoken from the pool
     * @param _poolId Id of the farming pool
     * @param _amount Amount of lp tokens to withdraw
     */
    function withdraw(uint256 _poolId, uint256 _amount)
        public
        nonReentrant
        whenNotPaused
    {
        require(_amount > 0, "FARMING_POOL: WITHDRAW_ZERO");

        PoolInfo storage pool = poolList[_poolId];
        UserInfo storage user = userInfo[_poolId][msg.sender];

        require(
            user.stakingBalance >= _amount,
            "FARMING_POOL: NO_ENOUGH_STAKING_BALANCE"
        );

        // Update if the pool is still farming
        // Users can withdraw even after the pool stopped
        if (isFarming[_poolId]) updatePool(_poolId);

        uint256 pending = (user.stakingBalance * pool.accFurionPerShare) /
            SCALE -
            user.rewardDebt;

        uint256 reward = _safeFurionTransfer(msg.sender, pending);
        emit Harvest(msg.sender, msg.sender, _poolId, reward);

        uint256 actualAmount = _safeLPTransfer(
            true,
            pool.lpToken,
            msg.sender,
            _amount
        );

        user.stakingBalance -= actualAmount;

        user.rewardDebt =
            (user.stakingBalance * pool.accFurionPerShare) /
            SCALE;

        emit Withdraw(msg.sender, _poolId, actualAmount);
    }

    /**
     * @notice Harvest the Furion reward and can be sent to another address
     * @param _poolId Id of the farming pool
     * @param _to Receiver of Furion rewards
     */
    function harvest(uint256 _poolId, address _to)
        public
        nonReentrant
        whenNotPaused
    {
        // Only update the pool when it is still in farming
        if (isFarming[_poolId]) updatePool(_poolId);

        PoolInfo memory pool = poolList[_poolId];
        UserInfo storage user = userInfo[_poolId][msg.sender];

        uint256 pendingReward = (user.stakingBalance * pool.accFurionPerShare) /
            SCALE -
            user.rewardDebt;

        require(pendingReward > 0, "FARMING_POOL: NO_PENDING_REWARD");

        // Update the reward debt
        user.rewardDebt =
            (user.stakingBalance * pool.accFurionPerShare) /
            SCALE;

        // Transfer the reward
        uint256 reward = _safeFurionTransfer(_to, pendingReward);

        emit Harvest(msg.sender, _to, _poolId, reward);
    }

    /**
     * @notice Update the pool's reward status
     * @param _poolId Id of the farming pool
     */
    function updatePool(uint256 _poolId) public {
        PoolInfo storage pool = poolList[_poolId];
        if (block.timestamp <= pool.lastRewardTimestamp) {
            return;
        }

        uint256 lpSupply = IERC20(pool.lpToken).balanceOf(address(this));

        // No LP deposited, then just update the lastRewardTimestamp
        if (lpSupply == 0) {
            pool.lastRewardTimestamp = block.timestamp;
            return;
        }

        uint256 timePassed = block.timestamp - pool.lastRewardTimestamp;

        uint256 basicReward = timePassed * pool.basicFurionPerSecond;

        pool.accFurionPerShare += (basicReward * SCALE) / lpSupply;

        // Don't forget to set the farming pool as minter
        furion.mintFurion(address(this), basicReward);

        pool.lastRewardTimestamp = block.timestamp;

        emit PoolUpdated(_poolId, pool.accFurionPerShare);
    }

    /**
     * @notice Update all farming pools (except for those stopped ones)
     * @dev Can be called by anyone
     *      Only update those active pools
     */
    function massUpdatePools() public {
        uint256 length = poolList.length;
        for (uint256 poolId; poolId < length; poolId++) {
            if (isFarming[poolId] == false) continue;
            else updatePool(poolId);
        }
    }

    // ---------------------------------------------------------------------------------------- //
    // *********************************** View Functions ************************************* //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice Check the amount of pending Furion reward
     * @param _poolId PoolId of this farming pool
     * @param _user User address
     * @return pendingFurionAmount Amount of pending Furion
     */
    function pendingFurion(uint256 _poolId, address _user)
        external
        view
        returns (uint256)
    {
        PoolInfo memory poolInfo = poolList[_poolId];

        if (
            poolInfo.lastRewardTimestamp == 0 ||
            block.timestamp < poolInfo.lastRewardTimestamp ||
            block.timestamp < startTimestamp
        ) return 0;

        UserInfo memory user = userInfo[_poolId][_user];

        // Total lp token balance
        uint256 lpBalance = IERC20(poolInfo.lpToken).balanceOf(address(this));

        // Accumulated shares to be calculated
        uint256 accFurionPerShare = poolInfo.accFurionPerShare;

        if (lpBalance == 0) return 0;
        else {
            // If the pool is still farming, update the info
            if (isFarming[_poolId]) {
                // Deigs amount given to this pool
                uint256 timePassed = block.timestamp -
                    poolInfo.lastRewardTimestamp;
                uint256 basicReward = poolInfo.basicFurionPerSecond *
                    timePassed;
                // Update accFurionPerShare
                // LPToken may have different decimals
                accFurionPerShare += (basicReward * SCALE) / lpBalance;
            }

            // If the pool has stopped, not update the info
            uint256 pending = (user.stakingBalance * accFurionPerShare) /
                SCALE -
                user.rewardDebt;

            return pending;
        }
    }

    /**
     * @notice Get the total pool list
     * @return pooList Total pool list
     */
    function getPoolList() external view returns (PoolInfo[] memory) {
        return poolList;
    }

    /**
     * @notice Get a user's balance
     * @param _poolId Id of the pool
     * @param _user User address
     * @return balance User's balance (lpToken)
     */
    function getUserBalance(uint256 _poolId, address _user)
        external
        view
        returns (uint256)
    {
        return userInfo[_poolId][_user].stakingBalance;
    }

    // ---------------------------------------------------------------------------------------- //
    // ************************************* Set Functions ************************************ //
    // ---------------------------------------------------------------------------------------- //

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Set the start block timestamp
     * @param _startTimestamp New start block timestamp
     */
    function setStartTimestamp(uint256 _startTimestamp)
        external
        onlyOwner
        whenNotPaused
    {
        // Can only be set before any pool is added
        require(_nextPoolId == 1, "ALREADY_HAVING_POOLS");

        startTimestamp = _startTimestamp;
        emit StartTimestampChanged(_startTimestamp);
    }

    // ---------------------------------------------------------------------------------------- //
    // ********************************** Internal Functions ********************************** //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice Check if a lptoken has been added into the pool before
     * @dev This can also be written as a modifier
     * @param _lpToken LP token address
     * @return _isInPool Wether this lp is already in pool
     */
    function _alreadyInPool(address _lpToken)
        internal
        view
        returns (bool _isInPool)
    {
        uint256 poolId = poolMapping[_lpToken];

        _isInPool = (poolId != 0) ? true : false;
    }

    /**
     * @notice Safe Furion transfer (check if the pool has enough Furion token, if not, transfer balance)
     * @param _to User's address
     * @param _amount Amount to transfer
     */
    function _safeFurionTransfer(address _to, uint256 _amount)
        internal
        returns (uint256)
    {
        uint256 poolFurionBalance = furion.balanceOf(address(this));
        require(poolFurionBalance > 0, "FARMING_POOL: NO_FUR_IN_POOL");

        if (_amount > poolFurionBalance) {
            furion.safeTransfer(_to, poolFurionBalance);
            return (poolFurionBalance);
        } else {
            furion.safeTransfer(_to, _amount);
            return _amount;
        }
    }

    /**
     * @notice Finish the transfer of LP Token
     * @dev The lp token may have loss during transfer
     * @param _out Whether the lp token is out
     * @param _lpToken LP token address
     * @param _user User address
     * @param _amount Amount of lp tokens
     */
    function _safeLPTransfer(
        bool _out,
        address _lpToken,
        address _user,
        uint256 _amount
    ) internal returns (uint256) {
        uint256 poolBalanceBefore = IERC20(_lpToken).balanceOf(address(this));

        if (_out) IERC20(_lpToken).safeTransfer(_user, _amount);
        else IERC20(_lpToken).safeTransferFrom(_user, address(this), _amount);

        uint256 poolBalanceAfter = IERC20(_lpToken).balanceOf(address(this));

        return
            _out
                ? poolBalanceBefore - poolBalanceAfter
                : poolBalanceAfter - poolBalanceBefore;
    }
}

contracts/furion-swap/FurionSwapV2Router.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../tokens/interfaces/IWETH.sol";
import "./interfaces/IFurionSwapV2Router.sol";
import "./interfaces/IFurionSwapFactory.sol";
import "./interfaces/IFurionSwapPair.sol";
import {IERC20Decimals} from "../utils/interfaces/IERC20Decimals.sol";

/*
//===================================//
 ______ _   _______ _____ _____ _   _ 
 |  ___| | | | ___ \_   _|  _  | \ | |
 | |_  | | | | |_/ / | | | | | |  \| |
 |  _| | | | |    /  | | | | | | . ` |
 | |   | |_| | |\ \ _| |_\ \_/ / |\  |
 \_|    \___/\_| \_|\___/ \___/\_| \_/
//===================================//
* /

/**
 * @title  FurionSwapRouter
 * @notice Router for the pool, you can add/remove liquidity or swap A for B.
 *         Swapping fee rate is 3, 99% of them is given to LP, and 1% to income maker
 *         Very similar logic with Uniswap V2.
 *
 */

contract FurionSwapV2Router is IFurionSwapV2Router {
    using SafeERC20 for IERC20;
    using SafeERC20 for IFurionSwapPair;

    // ---------------------------------------------------------------------------------------- //
    // ************************************* Variables **************************************** //
    // ---------------------------------------------------------------------------------------- //

    // Some other contracts
    address public immutable override factory;
    address public immutable override WETH;

    // ---------------------------------------------------------------------------------------- //
    // *************************************** Events ***************************************** //
    // ---------------------------------------------------------------------------------------- //

    event LiquidityAdded(
        address indexed pairAddress,
        uint256 amountA,
        uint256 amountB,
        uint256 liquidity
    );

    event LiquidityRemoved(
        address indexed pairAddress,
        uint256 amountA,
        uint256 amountB,
        uint256 liquidity
    );

    // ---------------------------------------------------------------------------------------- //
    // ************************************* Constructor ************************************** //
    // ---------------------------------------------------------------------------------------- //

    constructor(address _factory, address _weth) {
        factory = _factory;
        WETH = _weth;
    }

    // ---------------------------------------------------------------------------------------- //
    // ************************************** Modifiers *************************************** //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice Transactions are available only before the deadline
     * @param _deadline Deadline of the pool
     */
    modifier beforeDeadline(uint256 _deadline) {
        if (_deadline > 0) {
            if (msg.sender != IFurionSwapFactory(factory).incomeMaker()) {
                require(block.timestamp < _deadline, "expired transaction");
            }
        }
        _;
    }

    // ---------------------------------------------------------------------------------------- //
    // ************************************ Main Functions ************************************ //
    // ---------------------------------------------------------------------------------------- //

    receive() external payable {
        assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
    }

    /**
     * @notice Add liquidity function
     * @param _tokenA Address of tokenA
     * @param _tokenB Address of tokenB
     * @param _amountADesired Amount of tokenA desired
     * @param _amountBDesired Amount of tokenB desired
     * @param _amountAMin Minimum amoutn of tokenA
     * @param _amountBMin Minimum amount of tokenB
     * @param _to Address that receive the lp token, normally the user himself
     * @param _deadline Transaction will revert after this deadline
     * @return amountA Amount of tokenA to be input
     * @return amountB Amount of tokenB to be input
     * @return liquidity LP token to be mint
     */
    function addLiquidity(
        address _tokenA,
        address _tokenB,
        uint256 _amountADesired,
        uint256 _amountBDesired,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    )
        external
        beforeDeadline(_deadline)
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        )
    {
        (amountA, amountB) = _addLiquidity(
            _tokenA,
            _tokenB,
            _amountADesired,
            _amountBDesired,
            _amountAMin,
            _amountBMin
        );

        address pair = IFurionSwapFactory(factory).getPair(_tokenA, _tokenB);

        _transferFromHelper(_tokenA, msg.sender, pair, amountA);
        _transferFromHelper(_tokenB, msg.sender, pair, amountB);

        liquidity = IFurionSwapPair(pair).mint(_to);

        emit LiquidityAdded(pair, amountA, amountB, liquidity);
    }

    /**
     * @notice Add liquidity for pair where one token is ETH
     * @param _token Address of the other token
     * @param _amountTokenDesired Amount of token desired
     * @param _amountTokenMin Minimum amount of token
     * @param _amountETHMin Minimum amount of ETH
     * @param _to Address that receive the lp token, normally the user himself
     * @param _deadline Transaction will revert after this deadline
     * @return amountToken Amount of token to be input
     * @return amountETH Amount of ETH to be input
     * @return liquidity LP token to be mint
     */
    function addLiquidityETH(
        address _token,
        uint256 _amountTokenDesired,
        uint256 _amountTokenMin,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline
    )
        external
        payable
        beforeDeadline(_deadline)
        returns (
            uint256 amountToken,
            uint256 amountETH,
            uint256 liquidity
        )
    {
        (amountToken, amountETH) = _addLiquidity(
            _token,
            WETH,
            _amountTokenDesired,
            msg.value,
            _amountTokenMin,
            _amountETHMin
        );

        address pair = IFurionSwapFactory(factory).getPair(_token, WETH);

        _transferFromHelper(_token, msg.sender, pair, amountToken);
        IWETH(WETH).deposit{value: amountETH}();
        assert(IWETH(WETH).transfer(pair, amountETH));

        liquidity = IFurionSwapPair(pair).mint(_to);

        // refund dust eth, if any
        if (msg.value > amountETH)
            _safeTransferETH(msg.sender, msg.value - amountETH);

        emit LiquidityAdded(pair, amountToken, amountETH, liquidity);
    }

    /**
     * @notice Remove liquidity from the pool
     * @param _tokenA Address of token A
     * @param _tokenB Address of token B
     * @param _liquidity The lp token amount to be removed
     * @param _amountAMin Minimum amount of tokenA given out
     * @param _amountBMin Minimum amount of tokenB given out
     * @param _to User address
     * @param _deadline Deadline of this transaction
     * @return amount0 Amount of token0 given out
     * @return amount1 Amount of token1 given out, here amount0 & 1 is ordered
     */
    function removeLiquidity(
        address _tokenA,
        address _tokenB,
        uint256 _liquidity,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    )
        public
        override
        beforeDeadline(_deadline)
        returns (uint256 amount0, uint256 amount1)
    {
        address pair = IFurionSwapFactory(factory).getPair(_tokenA, _tokenB);

        IFurionSwapPair(pair).safeTransferFrom(msg.sender, pair, _liquidity); // send liquidity to pair

        // token0 < token1, corresponding amoount
        (amount0, amount1) = IFurionSwapPair(pair).burn(_to);

        (uint256 amount0Min, uint256 amount1Min) = _tokenA < _tokenB
            ? (_amountAMin, _amountBMin)
            : (_amountBMin, _amountAMin);

        require(amount0 >= amount0Min, "Insufficient amount for token0");
        require(amount1 >= amount1Min, "Insufficient amount for token1");

        emit LiquidityRemoved(pair, amount0, amount1, _liquidity);
    }

    /**
     * @notice Remove liquidity from the pool, one token is ETH
     * @param _token Address of the other token
     * @param _liquidity The lp token amount to be removed
     * @param _amountTokenMin Minimum amount of token given out
     * @param _amountETHMin Minimum amount of ETH given out
     * @param _to User address
     * @param _deadline Deadline of this transaction
     * @return amountToken Amount of token given out
     * @return amountETH Amount of ETH given out
     */
    function removeLiquidityETH(
        address _token,
        uint256 _liquidity,
        uint256 _amountTokenMin,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline
    )
        external
        beforeDeadline(_deadline)
        returns (uint256 amountToken, uint256 amountETH)
    {
        (amountToken, amountETH) = removeLiquidity(
            _token,
            WETH,
            _liquidity,
            _amountTokenMin,
            _amountETHMin,
            address(this),
            _deadline
        );

        // firstly make tokens inside the contract then transfer out
        _transferHelper(_token, _to, amountToken);

        IWETH(WETH).withdraw(amountETH);
        _safeTransferETH(_to, amountETH);
    }

    /**
     * @notice Swap exact tokens for another token, input is fixed
     * @param _amountIn Amount of input token
     * @param _amountOutMin Minimum amount of token given out
     * @param _path Address collection of trading path
     * @param _to Receiver of the output token, generally user address
     * @param _deadline Deadline of this transaction
     * @return amounts Amount of tokens
     */
    function swapExactTokensForTokens(
        uint256 _amountIn,
        uint256 _amountOutMin,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    )
        public
        override
        beforeDeadline(_deadline)
        returns (uint256[] memory amounts)
    {
        amounts = getAmountsOut(_amountIn, _path);

        require(
            amounts[amounts.length - 1] >= _amountOutMin,
            "FurionSwapV2Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );

        _transferFromHelper(
            _path[0],
            msg.sender,
            IFurionSwapFactory(factory).getPair(_path[0], _path[1]),
            amounts[0]
        );
        _swap(amounts, _path, _to);
    }

    /**
     * @notice Swap token for exact token, output is fixed
     * @param _amountOut Amount of output token
     * @param _amountInMax Maxmium amount of token in
     * @param _path Address collection of trading path
     * @param _to Receiver of the output token, generally user address
     * @param _deadline Deadline of this transaction
     * @return amounts Amount of tokens
     */
    function swapTokensForExactTokens(
        uint256 _amountOut,
        uint256 _amountInMax,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    )
        public
        override
        beforeDeadline(_deadline)
        returns (uint256[] memory amounts)
    {
        amounts = getAmountsIn(_amountOut, _path);

        require(
            amounts[0] <= _amountInMax,
            "FurionSwapV2Router: EXCESSIVE_INPUT_AMOUNT"
        );

        _transferFromHelper(
            _path[0],
            msg.sender,
            IFurionSwapFactory(factory).getPair(_path[0], _path[1]),
            amounts[0]
        );
        _swap(amounts, _path, _to);
    }

    /**
     * @notice Swap exact ETH for another token, input is fixed
     * @param _amountOutMin Minimum amount of output token
     * @param _path Address collection of trading path
     * @param _to Receiver of the output token, generally user address
     * @param _deadline Deadline of this transaction
     * @return amounts Amount of tokens
     */
    function swapExactETHForTokens(
        uint256 _amountOutMin,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    )
        external
        payable
        override
        beforeDeadline(_deadline)
        returns (uint256[] memory amounts)
    {
        require(_path[0] == WETH, "FurionSwapV2Router: INVALID_PATH");
        amounts = getAmountsOut(msg.value, _path);
        require(
            amounts[amounts.length - 1] >= _amountOutMin,
            "FurionSwapV2Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );

        IWETH(WETH).deposit{value: amounts[0]}();
        assert(
            IWETH(WETH).transfer(
                IFurionSwapFactory(factory).getPair(_path[0], _path[1]),
                amounts[0]
            )
        );
        _swap(amounts, _path, _to);
    }

    /**
     * @notice Swap token for exact ETH, output is fixed
     * @param _amountOut Amount of output token
     * @param _amountInMax Maxmium amount of token in
     * @param _path Address collection of trading path
     * @param _to Receiver of the output token, generally user address
     * @param _deadline Deadline of this transaction
     * @return amounts Amount of tokens
     */
    function swapTokensForExactETH(
        uint256 _amountOut,
        uint256 _amountInMax,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    )
        external
        override
        beforeDeadline(_deadline)
        returns (uint256[] memory amounts)
    {
        require(
            _path[_path.length - 1] == WETH,
            "FurionSwapV2Router: INVALID_PATH"
        );
        amounts = getAmountsIn(_amountOut, _path);
        require(
            amounts[0] <= _amountInMax,
            "FurionSwapV2Router: EXCESSIVE_INPUT_AMOUNT"
        );

        _transferFromHelper(
            _path[0],
            msg.sender,
            IFurionSwapFactory(factory).getPair(_path[0], _path[1]),
            amounts[0]
        );
        _swap(amounts, _path, address(this));
        IWETH(WETH).withdraw(amounts[amounts.length - 1]);
        _safeTransferETH(_to, amounts[amounts.length - 1]);
    }

    /**
     * @notice Swap exact tokens for ETH, input is fixed
     * @param _amountIn Amount of input token
     * @param _amountOutMin Minimum amount of output token
     * @param _path Address collection of trading path
     * @param _to Receiver of the output token, generally user address
     * @param _deadline Deadline of this transaction
     * @return amounts Amount of tokens
     */
    function swapExactTokensForETH(
        uint256 _amountIn,
        uint256 _amountOutMin,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    )
        external
        override
        beforeDeadline(_deadline)
        returns (uint256[] memory amounts)
    {
        require(
            _path[_path.length - 1] == WETH,
            "FurionSwapV2Router: INVALID_PATH"
        );
        amounts = getAmountsOut(_amountIn, _path);
        require(
            amounts[amounts.length - 1] >= _amountOutMin,
            "FurionSwapV2Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );

        _transferFromHelper(
            _path[0],
            msg.sender,
            IFurionSwapFactory(factory).getPair(_path[0], _path[1]),
            amounts[0]
        );

        _swap(amounts, _path, address(this));
        IWETH(WETH).withdraw(amounts[amounts.length - 1]);
        _safeTransferETH(_to, amounts[amounts.length - 1]);
    }

    /**
     * @notice Swap token for exact ETH, output is fixed
     * @param _amountOut Amount of output token
     * @param _path Address collection of trading path
     * @param _to Receiver of the output token, generally user address
     * @param _deadline Deadline of this transaction
     * @return amounts Amount of tokens
     */
    function swapETHForExactTokens(
        uint256 _amountOut,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    )
        external
        payable
        override
        beforeDeadline(_deadline)
        returns (uint256[] memory amounts)
    {
        require(_path[0] == WETH, "FurionSwapV2Router: INVALID_PATH");
        amounts = getAmountsIn(_amountOut, _path);
        require(
            amounts[0] <= msg.value,
            "FurionSwapV2Router: EXCESSIVE_INPUT_AMOUNT"
        );

        IWETH(WETH).deposit{value: amounts[0]}();
        assert(
            IWETH(WETH).transfer(
                IFurionSwapFactory(factory).getPair(_path[0], _path[1]),
                amounts[0]
            )
        );
        _swap(amounts, _path, _to);

        // refund dust eth, if any
        if (msg.value > amounts[0])
            _safeTransferETH(msg.sender, msg.value - amounts[0]);
    }

    // ---------------------------------------------------------------------------------------- //
    // *********************************** Helper Functions *********************************** //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice Fetch the reserves for a trading pair
     * @dev You need to sort the token order by yourself!
     *      No matter your input order, the return value will always start with lower address
     *      i.e. _tokenA < _tokenB ? (_tokenA, _tokenB) : (_tokenB, _tokenA)
     * @param _tokenA Address of tokenA
     * @param _tokenB Address of tokenB
     * @return reserve0 Reserve of token0,
     * @return reserve1 Reserve of token1
     */
    function getReserves(address _tokenA, address _tokenB)
        public
        view
        returns (uint112 reserve0, uint112 reserve1)
    {
        address pairAddress = IFurionSwapFactory(factory).getPair(
            _tokenA,
            _tokenB
        );

        // (token0 reserve, token1 reserve)
        (reserve0, reserve1) = IFurionSwapPair(pairAddress).getReserves();
    }

    /**
     * @notice Used when swap exact tokens for tokens (in is fixed)
     * @param _amountIn Amount of tokens put in
     * @param _tokenIn Address of the input token
     * @param _tokenOut Address of the output token
     * @return amountOut Amount of token out
     */
    function getAmountOut(
        uint256 _amountIn,
        address _tokenIn,
        address _tokenOut
    ) public view returns (uint256 amountOut) {
        (uint256 reserve0, uint256 reserve1) = getReserves(_tokenIn, _tokenOut);

        // Get the right order
        (uint256 reserveIn, uint256 reserveOut) = _tokenIn < _tokenOut
            ? (reserve0, reserve1)
            : (reserve1, reserve0);

        require(_amountIn > 0, "insufficient input amount");
        require(reserveIn > 0 && reserveOut > 0, "insufficient liquidity");

        // read fee rate from FurionSwapPair
        uint256 feeRate = IFurionSwapPair(
            IFurionSwapFactory(factory).getPair(_tokenIn, _tokenOut)
        ).feeRate();

        uint256 amountInWithFee = _amountIn * (1000 - feeRate);
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = reserveIn * 1000 + amountInWithFee;

        amountOut = numerator / denominator;
    }

    /**
     * @notice Used when swap tokens for exact tokens (out is fixed)
     * @param _amountOut Amount of tokens given out
     * @param _tokenIn Address of the input token
     * @param _tokenOut Address of the output token
     * @return amountIn Amount of token in
     */
    function getAmountIn(
        uint256 _amountOut,
        address _tokenIn,
        address _tokenOut
    ) public view returns (uint256 amountIn) {
        (uint256 reserve0, uint256 reserve1) = getReserves(_tokenIn, _tokenOut);

        // Get the right order
        (uint256 reserveIn, uint256 reserveOut) = _tokenIn < _tokenOut
            ? (reserve0, reserve1)
            : (reserve1, reserve0);

        require(_amountOut > 0, "insufficient output amount");
        require(reserveIn > 0 && reserveOut > 0, "insufficient liquidity");

        // read fee rate from FurionSwapPair
        uint256 feeRate = IFurionSwapPair(
            IFurionSwapFactory(factory).getPair(_tokenIn, _tokenOut)
        ).feeRate();

        uint256 numerator = reserveIn * (_amountOut) * 1000;
        uint256 denominator = (reserveOut - _amountOut) * (1000 - feeRate);

        amountIn = numerator / denominator + 1;
    }

    /**
     * @notice Used when swap exact tokens for tokens (in is fixed), multiple swap
     * @param _amountIn Amount of tokens put in
     * @param _path Path of trading routes
     * @return amounts Amount of tokens
     */
    function getAmountsOut(uint256 _amountIn, address[] memory _path)
        public
        view
        returns (uint256[] memory amounts)
    {
        require(_path.length >= 2, "FurionSwap: INVALID_PATH");
        amounts = new uint256[](_path.length);
        amounts[0] = _amountIn;
        for (uint256 i; i < _path.length - 1; i++) {
            amounts[i + 1] = getAmountOut(amounts[i], _path[i], _path[i + 1]);
        }
    }

    /**
     * @notice Used when swap exact tokens for tokens (out is fixed), multiple swap
     * @param _amountOut Amount of tokens get out
     * @param _path Path of trading routes
     * @return amounts Amount of tokens
     */
    function getAmountsIn(uint256 _amountOut, address[] memory _path)
        public
        view
        returns (uint256[] memory amounts)
    {
        require(_path.length >= 2, "FurionSwap: INVALID_PATH");
        amounts = new uint256[](_path.length);
        amounts[amounts.length - 1] = _amountOut;

        for (uint256 i = _path.length - 1; i > 0; i--) {
            amounts[i - 1] = getAmountIn(amounts[i], _path[i - 1], _path[i]);
        }
    }

    /**
     * @notice Given some amount of an asset and pair reserves
     *         returns an equivalent amount of the other asset
     * @dev Used when add or remove liquidity
     * @param _amountA Amount of tokenA
     * @param _reserveA Reserve of tokenA
     * @param _reserveB Reserve of tokenB
     * @return amountB Amount of tokenB
     */
    function quote(
        uint256 _amountA,
        uint256 _reserveA,
        uint256 _reserveB
    ) public pure returns (uint256 amountB) {
        require(_amountA > 0, "insufficient amount");
        require(_reserveA > 0 && _reserveB > 0, "insufficient liquidity");

        amountB = (_amountA * _reserveB) / _reserveA;
    }

    // ---------------------------------------------------------------------------------------- //
    // *********************************** Internal Functions ********************************* //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice Internal function to finish adding liquidity
     * @param _tokenA Address of tokenA
     * @param _tokenB Address of tokenB
     * @param _amountADesired Amount of tokenA to be added
     * @param _amountBDesired Amount of tokenB to be added
     * @param _amountAMin Minimum amount of tokenA
     * @param _amountBMin Minimum amount of tokenB
     * @return amountA Real amount of tokenA
     * @return amountB Real amount of tokenB
     */
    function _addLiquidity(
        address _tokenA,
        address _tokenB,
        uint256 _amountADesired,
        uint256 _amountBDesired,
        uint256 _amountAMin,
        uint256 _amountBMin
    ) private view returns (uint256 amountA, uint256 amountB) {
        (uint256 reserve0, uint256 reserve1) = getReserves(_tokenA, _tokenB);
        (uint256 reserveA, uint256 reserveB) = _tokenA < _tokenB
            ? (reserve0, reserve1)
            : (reserve1, reserve0);

        if (reserveA == 0 && reserveB == 0) {
            (amountA, amountB) = (_amountADesired, _amountBDesired);
        } else {
            uint256 amountBOptimal = quote(_amountADesired, reserveA, reserveB);
            if (amountBOptimal <= _amountBDesired) {
                require(amountBOptimal >= _amountBMin, "INSUFFICIENT_B_AMOUNT");
                (amountA, amountB) = (_amountADesired, amountBOptimal);
            } else {
                uint256 amountAOptimal = quote(
                    _amountBDesired,
                    reserveB,
                    reserveA
                );
                require(amountAOptimal <= _amountADesired, "UNAVAILABLE");
                require(amountAOptimal >= _amountAMin, "INSUFFICIENT_A_AMOUNT");
                (amountA, amountB) = (amountAOptimal, _amountBDesired);
            }
        }
    }

    /**
     * @notice Finish the erc20 transfer operation
     * @param _token ERC20 token address
     * @param _from Address to give out the token
     * @param _to Pair address to receive the token
     * @param _amount Transfer amount
     */
    function _transferFromHelper(
        address _token,
        address _from,
        address _to,
        uint256 _amount
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        // (bool success, bytes memory data) = _token.call(abi.encodeWithSelector(0x23b872dd, _from, _to, _amount));
        // require(success && (data.length == 0 || abi.decode(data, (bool))), "TransferHelper: TRANSFER_FROM_FAILED");
        IERC20(_token).safeTransferFrom(_from, _to, _amount);
    }

    /**
     * @notice Finish the erc20 transfer operation
     * @param _token ERC20 token address
     * @param _to Address to receive the token
     * @param _amount Transfer amount
     */
    function _transferHelper(
        address _token,
        address _to,
        uint256 _amount
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        // (bool success, bytes memory data) = _token.call(abi.encodeWithSelector(0x23b872dd, _from, _to, _amount));
        // require(success && (data.length == 0 || abi.decode(data, (bool))), "TransferHelper: TRANSFER_FROM_FAILED");
        IERC20(_token).safeTransfer(_to, _amount);
    }

    /**
     * @notice Finish the ETH transfer operation
     * @param _to Address to receive the token
     * @param _amount Transfer amount
     */
    function _safeTransferETH(address _to, uint256 _amount) internal {
        (bool success, ) = _to.call{value: _amount}("");
        require(success, "TransferHelper: ETH_TRANSFER_FAILED");
    }

    /**
     * @notice Finish swap process, requires the initial amount to have already been sent to the first pair
     * @param _amounts Amounts of token out for multiple swap
     * @param _path Address of tokens for multiple swap
     * @param _to Address of the final token receiver
     */
    function _swap(
        uint256[] memory _amounts,
        address[] memory _path,
        address _to
    ) private {
        for (uint256 i; i < _path.length - 1; i++) {
            // get token pair for each seperate swap
            (address input, address output) = (_path[i], _path[i + 1]);
            address token0 = input < output ? input : output;

            // get tokenOutAmount for each swap
            uint256 amountOut = _amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) = input == token0
                ? (uint256(0), amountOut)
                : (amountOut, uint256(0));

            address to = i < _path.length - 2
                ? IFurionSwapFactory(factory).getPair(output, _path[i + 2])
                : _to;

            IFurionSwapPair(IFurionSwapFactory(factory).getPair(input, output))
                .swap(amount0Out, amount1Out, to);
        }
    }
}

