// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./interfaces/IInterestRateModel.sol";

contract NormalInterestRateModel is IInterestRateModel {
    bool public constant IS_INTEREST_RATE_MODEL = true;

    uint256 private constant BASE = 1e18;

    /**
     * @notice The approximate number of blocks per year that is assumed by the interest rate model
     */
    uint256 public constant blocksPerYear = 2102400;

    /**
     * @notice The multiplier of utilization rate that gives the slope of the interest rate
     */
    uint256 public multiplierPerBlock;

    /**
     * @notice The base interest rate which is the y-intercept when utilization rate is 0
     */
    uint256 public baseRatePerBlock;

    event NewInterestParams(
        uint256 baseRatePerBlock,
        uint256 multiplierPerBlock
    );

    /**
     * @notice Construct an interest rate model
     * @param _baseRatePerYear The approximate target base APR, as a mantissa (scaled by BASE)
     * @param _multiplierPerYear The rate of increase in interest rate wrt utilization (scaled by BASE)
     */
    constructor(uint256 _baseRatePerYear, uint256 _multiplierPerYear) {
        baseRatePerBlock = _baseRatePerYear / blocksPerYear;
        multiplierPerBlock = _multiplierPerYear / blocksPerYear;

        emit NewInterestParams(baseRatePerBlock, multiplierPerBlock);
    }

    function isInterestRateModel() public pure returns (bool) {
        return IS_INTEREST_RATE_MODEL;
    }

    /**
     * @notice Calculates the utilization rate of the market: `borrows / (cash + borrows - reserves)`
     * @param _cash The amount of cash in the market
     * @param _borrows The amount of borrows in the market
     * @param _reserves The amount of reserves in the market (currently unused)
     * @return The utilization rate as a mantissa between [0, BASE]
     */
    function utilizationRate(
        uint256 _cash,
        uint256 _borrows,
        uint256 _reserves
    ) public pure returns (uint256) {
        // Utilization rate is 0 when there are no borrows
        if (_borrows == 0) {
            return 0;
        }

        return (_borrows * BASE) / (_cash + _borrows - _reserves);
    }

    /**
     * @notice Calculates the current borrow rate per block, with the error code expected by the market
     * @param _cash The amount of cash in the market
     * @param _borrows The amount of borrows in the market
     * @param _reserves The amount of reserves in the market
     * @return The borrow rate percentage per block as a mantissa (scaled by BASE)
     */
    function getBorrowRate(
        uint256 _cash,
        uint256 _borrows,
        uint256 _reserves
    ) public view override returns (uint256) {
        uint256 ur = utilizationRate(_cash, _borrows, _reserves);
        return ((ur * multiplierPerBlock) / BASE) + baseRatePerBlock;
    }

    /**
     * @notice Calculates the current supply rate per block
     * @param _cash The amount of cash in the market
     * @param _borrows The amount of borrows in the market
     * @param _reserves The amount of reserves in the market
     * @param _reserveFactorMantissa The current reserve factor for the market
     * @return The supply rate percentage per block as a mantissa (scaled by BASE)
     */
    function getSupplyRate(
        uint256 _cash,
        uint256 _borrows,
        uint256 _reserves,
        uint256 _reserveFactorMantissa
    ) public view override returns (uint256) {
        uint256 oneMinusReserveFactor = BASE - _reserveFactorMantissa;
        uint256 borrowRate = getBorrowRate(_cash, _borrows, _reserves);
        uint256 rateToPool = (borrowRate * oneMinusReserveFactor) / BASE;
        return
            (utilizationRate(_cash, _borrows, _reserves) * rateToPool) / BASE;
    }
}

contracts/money-market/JumpInterestRateM.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IInterestRateModel.sol";

contract JumpInterestRateModel is Ownable, IInterestRateModel {
    bool public constant IS_INTEREST_RATE_MODEL = true;

    uint256 private constant BASE = 1e18;

    /**
     * @notice The approximate number of blocks per year that is assumed by the interest rate model
     */
    uint256 public constant blocksPerYear = 2102400;

    /**
     * @notice The multiplier of utilization rate that gives the slope of the interest rate
     */
    uint256 public multiplierPerBlock;

    /**
     * @notice The base interest rate which is the y-intercept when utilization rate is 0
     */
    uint256 public baseRatePerBlock;

    /**
     * @notice The multiplierPerBlock after hitting a specified utilization point
     */
    uint256 public jumpMultiplierPerBlock;

    /**
     * @notice The utilization point at which the jump multiplier is applied
     */
    uint256 public kink;

    event NewInterestParams(
        uint256 baseRatePerBlock,
        uint256 multiplierPerBlock,
        uint256 jumpMultiplierPerBlock,
        uint256 kink
    );

    /**
     * @notice Construct an interest rate model
     * @param _baseRatePerYear The approximate target base APR, as a mantissa (scaled by BASE)
     * @param _multiplierPerYear The rate of increase in interest rate wrt utilization (scaled by BASE)
     * @param _jumpMultiplierPerYear The multiplierPerBlock after hitting a specified utilization point
     * @param _kink The utilization point at which the jump multiplier is applied
     */
    constructor(
        uint256 _baseRatePerYear,
        uint256 _multiplierPerYear,
        uint256 _jumpMultiplierPerYear,
        uint256 _kink
    ) {
        baseRatePerBlock = _baseRatePerYear / blocksPerYear;
        multiplierPerBlock =
            (_multiplierPerYear * BASE) /
            (blocksPerYear * _kink);
        jumpMultiplierPerBlock = _jumpMultiplierPerYear / blocksPerYear;
        kink = _kink;

        emit NewInterestParams(
            baseRatePerBlock,
            multiplierPerBlock,
            jumpMultiplierPerBlock,
            kink
        );
    }

    function isInterestRateModel() public pure returns (bool) {
        return IS_INTEREST_RATE_MODEL;
    }

    /**
     * @notice Internal function to update the parameters of the interest rate model
     * @param _baseRatePerYear The approximate target base APR, as a mantissa (scaled by BASE)
     * @param _multiplierPerYear The rate of increase in interest rate wrt utilization (scaled by BASE)
     * @param _jumpMultiplierPerYear The multiplierPerBlock after hitting a specified utilization point
     * @param _kink The utilization point at which the jump multiplier is applied
     */
    function updateJumpRateModel(
        uint256 _baseRatePerYear,
        uint256 _multiplierPerYear,
        uint256 _jumpMultiplierPerYear,
        uint256 _kink
    ) external onlyOwner {
        baseRatePerBlock = _baseRatePerYear / blocksPerYear;
        multiplierPerBlock =
            (_multiplierPerYear * BASE) /
            (blocksPerYear * _kink);
        jumpMultiplierPerBlock = _jumpMultiplierPerYear / blocksPerYear;
        kink = _kink;

        emit NewInterestParams(
            baseRatePerBlock,
            multiplierPerBlock,
            jumpMultiplierPerBlock,
            kink
        );
    }

    /**
     * @notice Calculates the utilization rate of the market: `borrows / (cash + borrows - reserves)`
     * @param _cash The amount of cash in the market
     * @param _borrows The amount of borrows in the market
     * @param _reserves The amount of reserves in the market (currently unused)
     * @return The utilization rate as a mantissa between [0, BASE]
     */
    function utilizationRate(
        uint256 _cash,
        uint256 _borrows,
        uint256 _reserves
    ) public pure returns (uint256) {
        // Utilization rate is 0 when there are no borrows
        if (_borrows == 0) {
            return 0;
        }

        return (_borrows * BASE) / (_cash + _borrows - _reserves);
    }

    /**
     * @notice Calculates the current borrow rate per block, with the error code expected by the market
     * @param _cash The amount of cash in the market
     * @param _borrows The amount of borrows in the market
     * @param _reserves The amount of reserves in the market
     * @return The borrow rate percentage per block as a mantissa (scaled by BASE)
     */
    function getBorrowRate(
        uint256 _cash,
        uint256 _borrows,
        uint256 _reserves
    ) public view returns (uint256) {
        uint256 util = utilizationRate(_cash, _borrows, _reserves);

        if (util <= kink) {
            return ((util * multiplierPerBlock) / BASE) + baseRatePerBlock;
        } else {
            uint256 normalRate = ((kink * multiplierPerBlock) / BASE) +
                baseRatePerBlock;
            uint256 excessUtil = util - kink;
            return ((excessUtil * jumpMultiplierPerBlock) / BASE) + normalRate;
        }
    }

    /**
     * @notice Calculates the current supply rate per block
     * @param _cash The amount of cash in the market
     * @param _borrows The amount of borrows in the market
     * @param _reserves The amount of reserves in the market
     * @param _reserveFactorMantissa The current reserve factor for the market
     * @return The supply rate percentage per block as a mantissa (scaled by BASE)
     */
    function getSupplyRate(
        uint256 _cash,
        uint256 _borrows,
        uint256 _reserves,
        uint256 _reserveFactorMantissa
    ) public view returns (uint256) {
        uint256 oneMinusReserveFactor = BASE - _reserveFactorMantissa;
        uint256 borrowRate = getBorrowRate(_cash, _borrows, _reserves);
        uint256 rateToPool = (borrowRate * oneMinusReserveFactor) / BASE;
        return
            (utilizationRate(_cash, _borrows, _reserves) * rateToPool) / BASE;
    }
}

contracts/money-market/FEther.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./TokenBase.sol";

contract FEther is TokenBase {
    function initialize(
        address _riskManager,
        address _interestRateModel,
        address _priceOracle,
        address _checker
    ) public initializer {
        __TokenBase_init(
            _riskManager,
            _interestRateModel,
            _priceOracle,
            _checker,
            "Furion Ether",
            "fETH"
        );
    }

    /**
     * @notice Sender supplies ETH into the market and receives fETH in exchange
     * @dev Reverts upon any failure
     */
    function supply() external payable {
        // Params: supplier, supply amount
        supplyInternal(msg.sender, msg.value);
    }

    /**
     * @notice Sender redeems fETH in exchange for ETH.
     * @dev Accrues interest whether or not the operation succeeds, unless reverted
     * @param _redeemTokens The number of fETH to redeem into underlying
     */
    function redeem(uint256 _redeemTokens) external {
        // Params: redeemer, tokens supplied for redemption, amount of underlying to receive
        redeemInternal(msg.sender, _redeemTokens, 0);
    }

    /**
     * @notice Sender redeems fETH in exchange for a specified amount of ETH.
     * @dev Accrues interest whether or not the operation succeeds, unless reverted
     * @param _redeemAmount The amount of ETH to redeem
     */
    function redeemUnderlying(uint256 _redeemAmount) external {
        // Params: redeemer, tokens supplied for redemption, amount of underlying to receive
        redeemInternal(msg.sender, 0, _redeemAmount);
    }

    /**
     * @notice Sender borrows ETH from the protocol to their own address
     * @param _borrowAmount The amount of ETH to borrow
     */
    function borrow(uint256 _borrowAmount) external {
        // Params: borrower, borrow amount
        borrowInternal(msg.sender, _borrowAmount);
    }

    /**
     * @notice Sender repays their own borrow
     * @dev Reverts upon any failure
     */
    function repayBorrow() external payable {
        // Params: payer, borrower, repay amount
        repayBorrowInternal(msg.sender, msg.sender, msg.value);
    }

    /**
     * @notice Sender repays a borrow belonging to borrower
     * @dev Reverts upon any failure
     * @param _borrower the account with the debt being payed off
     */
    function repayBorrowBehalf(address _borrower) external payable {
        // Params: payer, borrower, repay amount
        repayBorrowInternal(msg.sender, _borrower, msg.value);
    }

    function liquidateBorrow(address _borrower, address _fTokenCollateral)
        external
        payable
    {
        liquidateBorrowInternal(
            msg.sender,
            _borrower,
            msg.value,
            _fTokenCollateral
        );
    }

    /******************************* Safe Token *******************************/

    function doTransferIn(address _from, uint256 _amount) internal override {
        require(msg.sender == _from, "FEther: Not owner of account");
        require(msg.value == _amount, "FEther: Not enough ETH supplied");

        totalCash += _amount;
    }

    function doTransferOut(address payable _to, uint256 _amount)
        internal
        override
    {
        _to.transfer(_amount);

        totalCash -= _amount;
    }
}

contracts/aggregate-pool/FurionPricingOr.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract FurionPricingOracle is Ownable {
    mapping(bytes32 => AggregatorV3Interface) priceFeed;

    mapping(address => uint256[]) public prices;

    // 0: Normal - 1: Mid - 2: Rare
    // NFT token address to token id to price
    mapping(address => mapping(uint256 => uint256)) public priceLevel;
    mapping(address => bytes32) public quoteToken;

    event PriceUpdated(address token, uint256 level, uint256 price);

    function setPriceFeed(string calldata _name, address _feed)
        external
        onlyOwner
    {
        bytes32 tokenId = keccak256(abi.encodePacked(_name));

        // Set price feed info
        priceFeed[tokenId] = AggregatorV3Interface(_feed);
    }

    function getNFTPrice(address _token, uint256 _id)
        external
        view
        returns (uint256 price)
    {
        uint256 level = getPriceLevel(_token, _id);

        return prices[_token][level];
    }

    function getPriceLevel(address _token, uint256 _id)
        public
        view
        returns (uint256)
    {
        return priceLevel[_token][_id];
    }

    // update price to all levels under certain token address
    function updatePrices(address _token, uint256[] memory _prices)
        public
        onlyOwner
    {
        uint256 length = prices[_token].length;

        require(length > 0, "Price not initialized");

        require(length == _prices.length, "Length mismatch");

        for (uint256 i; i < length; ) {
            prices[_token][i] = _prices[i];

            unchecked {
                ++i;
            }

            emit PriceUpdated(_token, i, _prices[i]);
        }
    }

    function initPrice(address _token, uint256 _levels) external onlyOwner {
        require(prices[_token].length == 0, "Already initialized");

        for (uint256 i; i < _levels; ) {
            prices[_token].push(0);
            unchecked {
                ++i;
            }
        }
    }

    function updatePrice(
        address _token,
        uint256 _level,
        uint256 _price
    ) public onlyOwner {
        require(prices[_token].length > 0, "Price not initialized");

        prices[_token][_level] = _price;
        emit PriceUpdated(_token, _level, _price);
    }

    function _getChainlinkPrice(bytes32 _id) internal view returns (uint256) {
        (, int256 price, , , ) = priceFeed[_id].latestRoundData();

        return uint256(price);
    }
}

